mapper.select:
SELECT r.id, r.memberId, r.title, r.body, r.price, r.inserted, m.phoneNum, r.term, r.state
FROM Request r JOIN Member m ON r.memberId = m.id
WHERE r.id = #{id}

mapper.update:
	UPDATE Request
  	SET title = #{title}, body = #{body}, price = #{price}, state = #{state}
  	WHERE id = #{id}
